mapper.update:
UPDATE MEMBER SET
                  M_PASSWD =#{m_passwd}
                , M_EMAIL =#{m_email}
                , M_PHONE =#{m_phone}
                , M_ZIPCODE =#{m_zipcode}
                , M_ADDRESS =#{m_address}
                , M_ADDRESS2 =#{m_address2}

                WHERE M_ID =#{m_id}
