mapper.select:
SELECT * FROM (SELECT ROWNUM AS RR, A.* FROM (SELECT N_NO, N_SUBJECT, N_CONTENT, N_READCOUNT, N_WRITER, N_VDATE, N_REGDATE, N_STATE FROM NOTICE WHERE N_STATE ='A'
AND N_NO = #{n_no}
ORDER BY N_NO DESC) A)
WHERE RR BETWEEN #{start} and #{end}

